        }
    },
    en: {
        // VideoSection translations
        videoSection: {
            title: "Our Videos",
            subtitle: "Discover the world of professional truck driving",
            videos: [
                {
                    title: "Life of a Long-Haul Driver",
                    description: "Dive into the exciting world of truckers and their adventures on the roads lorem1000 ipsum dolor sit amet consectetur adipiscing elit"
                },
                {
                    title: "Career Opportunities",
                    description: "Discover numerous opportunities in the transportation industry"
                }
            ],
