            ],
            browserNotSupported: "Su navegador no soporta la etiqueta de video."
        },
        // Language selector
        language: {
            select: "Seleccionar Idioma",
            russian: "Русский",
            english: "English",
            spanish: "Español"
        }
    }
};

export const getTranslation = (language, path) => {
    const keys = path.split('.');
    let result = translations[language];
    
    for (const key of keys) {
        if (result && result[key]) {
            result = result[key];
        } else {
            // Fallback to Russian if translation not found
            result = translations.ru;
            for (const fallbackKey of keys) {
                if (result && result[fallbackKey]) {
                    result = result[fallbackKey];
                } else {
                    return path; // Return the path if no translation found
                }
            }
            break;
        }
    }
    
    return result;
};
