import React, { useState } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { getTranslation } from '../translations/translations';
import './LanguageSelector.css';

const LanguageSelector = () => {
    const { currentLanguage, changeLanguage } = useLanguage();
    const [isOpen, setIsOpen] = useState(false);
    
    console.log('LanguageSelector rendering, currentLanguage:', currentLanguage);

    const languages = [
        { code: 'en', name: getTranslation(currentLanguage, 'language.english'), flag: 'ðŸ‡ºðŸ‡¸' },
        { code: 'es', name: getTranslation(currentLanguage, 'language.spanish'), flag: 'ðŸ‡ªðŸ‡¸' }
    ];

    const currentLang = languages.find(lang => lang.code === currentLanguage);

    const handleLanguageChange = (langCode) => {
        changeLanguage(langCode);
        setIsOpen(false);
    };

    return (
        <div className="language-selector">
            <button 
                className="language-button"
                onClick={() => setIsOpen(!isOpen)}
                aria-label={getTranslation(currentLanguage, 'language.select')}
            >
                <span className="flag">{currentLang?.flag}</span>
                <span className="language-code">{currentLanguage.toUpperCase()}</span>
                <span className={`chevron ${isOpen ? 'rotate' : ''}`}>â–¼</span>
            </button>
            
            {isOpen && (
                <div className="language-dropdown">
                    {languages.map((language) => (
                        <button
                            key={language.code}
                            className={`language-option ${currentLanguage === language.code ? 'active' : ''}`}
                            onClick={() => handleLanguageChange(language.code)}
                        >
                            <span className="flag">{language.flag}</span>
                            <span className="language-name">{language.name}</span>
                        </button>
                    ))}
                </div>
            )}
        </div>
    );
};

export default LanguageSelector;
