import React, { useState, useEffect, useRef } from 'react';
import { useInView } from 'react-intersection-observer';
import './VideoSection.css';

const VideoSection = () => {
    const { ref, inView } = useInView({
        threshold: 0.1,
        triggerOnce: true
    });

    const [activeVideo, setActiveVideo] = useState(0);
    const videoPlayerRef = useRef(null);
    const mainVideoRef = useRef(null);

    const videos = [
        {
            src: '/img/video 1.mp4',
            title: 'Жизнь водителя дальнобойщика',
            description: 'Узнайте о повседневной жизни профессиональных водителей грузовиков'
        },
        {
            src: '/img/video 2.mp4',
            title: 'Карьерные возможности',
            description: 'Откройте для себя множество возможностей в транспортной индустрии'
        }
    ];

    // Функция для создания блюр-фона из видео
    const createVideoBackground = (videoSrc) => {
        return `url("${videoSrc}#t=1")`;
    };

    useEffect(() => {
        if (videoPlayerRef.current) {
            const videoSrc = videos[activeVideo].src;
            videoPlayerRef.current.style.setProperty('--video-bg', createVideoBackground(videoSrc));
        }
    }, [activeVideo, videos]);

    return (
        <section id="videos" className="video-section">
            <div className="container">
                <div className="section-header">
                    <h2>Наши Видео</h2>
                    <p>Познакомьтесь с миром профессионального вождения грузовиков</p>
                </div>
                
                <div ref={ref} className={`video-content ${inView ? 'fade-in visible' : 'fade-in'}`}>
                    <div ref={videoPlayerRef} className="video-player">
                        <video
                            ref={mainVideoRef}
                            key={activeVideo}
                            controls
                            className="main-video"
                            preload="metadata"
                        >
                            <source src={videos[activeVideo].src} type="video/mp4" />
                            Ваш браузер не поддерживает видео тег.
                        </video>
                        
                        <div className="video-info">
                            <h3>{videos[activeVideo].title}</h3>
                            <p>{videos[activeVideo].description}</p>
                        </div>
                    </div>
                    
                    <div className="video-thumbnails">
                        {videos.map((video, index) => (
                            <div
                                key={index}
                                className={`video-thumbnail ${activeVideo === index ? 'active' : ''}`}
                                onClick={() => setActiveVideo(index)}
                                style={{ '--thumbnail-bg': createVideoBackground(video.src) }}
                            >
                                <video
                                    muted
                                    className="thumbnail-video"
                                    preload="metadata"
                                    onMouseEnter={(e) => {
                                        e.target.currentTime = 1;
                                        e.target.play();
                                    }}
                                    onMouseLeave={(e) => {
                                        e.target.pause();
                                        e.target.currentTime = 1;
                                    }}
                                >
                                    <source src={video.src} type="video/mp4" />
                                </video>
                                <div className="thumbnail-overlay">
                                    <i className="fas fa-play"></i>
                                </div>
                                <div className="thumbnail-info">
                                    <h4>{video.title}</h4>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </section>
    );
};

export default VideoSection;
