import React, { useState, useEffect, useRef } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { getTranslation } from '../translations/translations';
import './LanguageSelector.css';

const LanguageSelector = () => {
    const { currentLanguage, changeLanguage } = useLanguage();
    const [isOpen, setIsOpen] = useState(false);
    const dropdownRef = useRef(null);

    // Close dropdown when clicking outside
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setIsOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const languages = [
        { code: 'ru', name: getTranslation(currentLanguage, 'language.russian'), flag: 'RU', color: '#0052cc' },
        { code: 'en', name: getTranslation(currentLanguage, 'language.english'), flag: 'EN', color: '#c8102e' },
        { code: 'es', name: getTranslation(currentLanguage, 'language.spanish'), flag: 'ES', color: '#aa151b' }
    ];

    const currentLang = languages.find(lang => lang.code === currentLanguage);

    const handleLanguageChange = (langCode) => {
        changeLanguage(langCode);
        setIsOpen(false);
    };

    return (
        <div className="language-selector" ref={dropdownRef}>
            <button 
                className="language-button"
                onClick={() => setIsOpen(!isOpen)}
                aria-label={getTranslation(currentLanguage, 'language.select')}
            >
                <span 
                    className="flag-indicator" 
                    style={{ backgroundColor: currentLang?.color }}
                ></span>
                <span className="language-code">{currentLanguage.toUpperCase()}</span>
                <span className={`chevron ${isOpen ? 'rotate' : ''}`}>â–¼</span>
            </button>
            
            {isOpen && (
                <div className="language-dropdown">
                    {languages.map((language) => (
                        <button
                            key={language.code}
                            className={`language-option ${currentLanguage === language.code ? 'active' : ''}`}
                            onClick={() => handleLanguageChange(language.code)}
                        >
                            <span className="flag">{language.flag}</span>
                            <span className="language-name">{language.name}</span>
                        </button>
                    ))}
                </div>
            )}
        </div>
    );
};

export default LanguageSelector;
